import React, { useEffect, useState } from "react";
import { Card, Button } from "react-bootstrap";

const SpecialOffers = () => {
  const [left, setLeft] = useState(-200);
  const [btnLeft, setBtnLeft] = useState(0);
  return (
    <div
      style={{
        position: "fixed",
        top: "200px",
        left: btnLeft + "px",
        borderTopRightRadius: "20px",
        borderBottomRightRadius: "20px",
        height: "150px",
        width: "50px",
        background: "rgb(33, 207, 147)",
        zIndex: "2000",
        WebkitTransitionDuration: "0.3s",
        MozTransitionDuration: "0.3s",
        OTransitionDuration: "0.3s",
        transitionDuration: "0.3s"
      }}
      onMouseOver={() => {
        setLeft(0);
        setBtnLeft(200);
      }}
      onMouseLeave={() => {
        setLeft(-200);
        setBtnLeft(0);
      }}
    >
      <p
        style={{
          textOrientation: "mixed",
          writingMode: "tb-rl",
          fontWeight: "bold",
          transform: "rotate(-180deg)",
          //   color: 'rgb(45, 22, 89)',
          color: "rgb(255, 255, 255)",
          paddingBottom: "10px",
          cursor: "grab"
        }}
      >
        2.9 Special Rate
      </p>
      <div
        style={{
          width: "200px",
        //   height: "300px",
          position: "fixed",
          left: left + "px",
          top: "200px",
          background: "rgba(255, 255, 255)",
          WebkitTransitionDuration: "0.3s",
          MozTransitionDuration: "0.3s",
          OTransitionDuration: "0.3s",
          transitionDuration: "0.3s"
        }}
      >
        <div style={{ margin: "10px" }}>
          <Card style={{

          }}>
            <Card.Body>
              <Card.Title style={{ fontWeight: "600", color: "#2d1659" }}>
                2.9 Special Rates
              </Card.Title>
              <Card.Text style={{ color: "#766f9b" }}>
                You are more than welcome to try our special rates
              </Card.Text>
            </Card.Body>
            <Button
              style={{
                backgroundColor: "#21cf93",
                border: "1px solid #21cf93",
                margin: "10px",
                borderRadius: "30px",
                fontSize: "1em",
                fontWeight: "bold"
              }}
              size="lg"
            >
              Apply
            </Button>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default SpecialOffers;

